INSERT INTO Meals VALUES (NULL, 'Honey Teriyaki Salmon', NULL, 17, 26, 'Mix all the ingredients in the Honey Teriyaki Glaze together. Whisk to blend well. Combine the salmon and the Glaze together.\r\n\r\nHeat up a skillet on medium-low heat. Add the oil, Pan-fry the salmon on both sides until it\u2019s completely cooked inside and the glaze thickens.\r\n\r\nGarnish with sesame and serve immediately.', 'https:\/\/www.themealdb.com\/images\/media\/meals\/xxyupu1468262513.jpg', 'https:\/\/www.youtube.com\/watch?v=4MpYuaJsvRw', 10);
